import UIKit
import Services

enum AuthorsList {

    // MARK: Use cases
  
    enum FetchAuthors {
        struct Response {
            var authors: Authors
        }
        struct ViewModel {
            var displayedAuthors: [DisplayedAuthor]
        }
    }

    enum Error {
        struct Response {
            var message: String
        }
        struct ViewModel {
            var title: String
            var message: String
        }
    }
    
    struct DisplayedAuthor {
        var id: Int
        var name: String
        var avatarUrl: String
    }
}
